css:
.github-CommitView {
  padding: 10px;
  border-top: 1px solid rgba(0, 0, 0, 0.5);
  flex: 0 0 auto;
}
.github-CommitView-editor {
  position: relative;
  border: 1px solid rgba(0, 0, 0, 0.5);
  border-radius: 2px;
  padding: 5px;
  font-size: 11px;
  background-color: #1d1f21;
}
.github-CommitView-editor.hidden {
  display: none;
}
.github-CommitView-editor atom-text-editor {
  height: 100px;
}
.github-CommitView-editor atom-text-editor .cursor-line.cursor-line.cursor-line {
  background-color: transparent;
}
.github-CommitView-expandButton {
  position: absolute;
  right: 0;
  bottom: 0;
  padding: 5px;
  line-height: 1;
  border: none;
  background-color: #1d1f21;
  cursor: default;
  opacity: .3;
}
.github-CommitView-expandButton:hover {
  opacity: 1;
}
.github-CommitView-expandButton:active {
  opacity: .5;
}
.github-CommitView-expandButton:before {
  margin: 0;
}
.github-CommitView-bar {
  display: flex;
  align-items: center;
  margin-top: 10px;
}
.github-CommitView-label {
  margin-right: 10px;
}
.github-CommitView-button {
  flex: 1;
  overflow: hidden;
  text-overflow: ellipsis;
  cursor: default;
}
.github-CommitView-button.is-secondary {
  flex: 0 1 auto;
  margin-right: 10px;
}
.github-CommitView-remaining-characters {
  width: 1.25em;
  text-align: right;
  margin-left: 10px;
  color: #555;
}
.github-CommitView-remaining-characters.is-warning {
  color: #ff982d;
}
.github-CommitView-remaining-characters.is-error {
  color: #c00;
}
.github-CommitView-editor.is-expanded {
  background-color: transparent;
}
.github-CommitView-editor.is-expanded atom-text-editor {
  display: none;
}
.github-CommitView-editor.is-expanded .github-CommitView-expandButton {
  position: relative;
  width: 100%;
  line-height: inherit;
  background-color: transparent;
  opacity: .5;
}
.github-CommitView-editor.is-expanded .github-CommitView-expandButton::before {
  margin-right: 5px;
}
.github-CommitView-editor.is-expanded .github-CommitView-expandButton::after {
  content: "Toggle expanded commit message";
}
.github-CommitView-editor.is-expanded .github-CommitView-expandButton:active {
  opacity: .3;
}
.theme-one-dark-ui .github-CommitView-editor atom-text-editor.is-focused,
.theme-one-light-ui .github-CommitView-editor atom-text-editor.is-focused {
  box-shadow: none;
}
.theme-one-dark-ui .github-CommitView-editor atom-text-editor.is-focused:before,
.theme-one-light-ui .github-CommitView-editor atom-text-editor.is-focused:before {
  content: "";
  position: absolute;
  top: -2px;
  left: -2px;
  right: -2px;
  bottom: -2px;
  border: 2px solid;
  border-color: inherit;
  border-radius: 2px;
}
