css:
.quokka-status {
  outline: 0 !important;
  display: inline-block;
  margin-left: 10px;
  margin-right: 10px;
  vertical-align: middle;
  width: 14px;
  height: 14px;
}
.quokka-status.progress {
  position: relative;
  width: 0;
  padding: 7px;
  background: linear-gradient(#aaa 30%, transparent 30%, transparent 70%, rgba(170, 170, 170, 0.4) 70%), linear-gradient(to left, rgba(170, 170, 170, 0.2) 30%, transparent 30%, transparent 70%, rgba(170, 170, 170, 0.8) 70%);
  background-repeat: no-repeat;
  background-size: 10% 100%, 100% 10%;
  background-position: 50% 0%, 0 50%;
  -webkit-animation: loading 0.7s infinite steps(8);
}
.quokka-status.progress:after {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image: linear-gradient(rgba(170, 170, 170, 0.1) 30%, transparent 30%, transparent 70%, rgba(170, 170, 170, 0.5) 70%), linear-gradient(to left, rgba(170, 170, 170, 0.3) 30%, transparent 30%, transparent 70%, rgba(170, 170, 170, 0.9) 70%);
  background-repeat: no-repeat;
  background-size: 10% 100%, 100% 10%;
  background-position: 50% 0%, 0 50%;
  -webkit-transform: rotate(45deg);
}
@-webkit-keyframes loading {
  100% {
    -webkit-transform: rotate(1turn);
  }
}
@keyframes loading {
  100% {
    transform: rotate(1turn);
  }
}
.quokka-panel-view {
  position: relative;
  width: 100%;
  overflow: hidden;
  min-height: 40px;
  height: 200px;
  z-index: 2;
}
.quokka-panel-view .quokka-panel-view-resizer {
  position: absolute;
  left: 0;
  right: 0;
  height: 10px;
  cursor: row-resize;
  z-index: 3;
}
.quokka-panel-view .quokka-panel-view-container {
  padding: 5px;
  padding-left: 15px;
  height: 100%;
  width: 100%;
  display: flex;
  flex-direction: column;
}
.quokka-panel-view .quokka-panel-view-container .quokka-panel-view-header {
  cursor: default;
  -webkit-user-select: none;
  padding: 2.5px 5px;
}
.quokka-panel-view .quokka-panel-view-container .quokka-panel-view-header .header-item {
  margin: 2.5px 0;
}
.quokka-panel-view .quokka-panel-view-container .quokka-panel-view-header .subtle-info-message {
  color: #555;
}
.quokka-panel-view .quokka-panel-view-container .quokka-panel-view-header .icons {
  padding-left: 5px;
}
.quokka-panel-view .quokka-panel-view-container .quokka-panel-view-header .icons .icon {
  cursor: pointer;
}
.quokka-panel-view .quokka-panel-view-container .quokka-panel-view-header .icons .icon:before {
  font-size: 14px;
}
.quokka-panel-view .quokka-panel-view-container .quokka-panel-view-header .icons .icon-x {
  color: #aaa;
}
.quokka-panel-view .quokka-panel-view-container .quokka-panel-view-content {
  flex: 1;
  display: flex;
  flex-direction: row;
  font-size: 11px;
}
.quokka-results-view {
  max-height: 600px;
  overflow: auto;
  padding: 2.5px 5px;
  padding-right: 10px;
  flex: 1;
}
.quokka-results-view > .error .header,
.quokka-results-view .diff {
  margin-bottom: 7.5px;
}
.quokka-results-view > .error,
.quokka-results-view .message,
.quokka-results-view .diff-message {
  margin-bottom: 15px;
}
.quokka-results-view a.loc,
.quokka-results-view a.install-package-quokka,
.quokka-results-view a.install-package-project {
  color: #5293d8;
}
.quokka-results-view a.loc:hover,
.quokka-results-view a.install-package-quokka:hover,
.quokka-results-view a.install-package-project:hover {
  color: #5293d8;
}
.quokka-results-view a.text {
  text-decoration: none;
  cursor: text;
}
.quokka-results-view .pad {
  margin-left: 10px;
}
.quokka-results-view .error > .header {
  color: #c00;
}
.quokka-results-view .message > .line,
.quokka-results-view .error > .line {
  display: inline;
  margin-left: 0;
  margin-top: 0;
  color: #555;
}
.quokka-results-view .error > .line {
  margin-left: 10px;
  display: block;
}
.quokka-results-view .text {
  color: #fff;
}
.quokka-results-view .warn,
.quokka-results-view .warn .text,
.quokka-results-view .context {
  color: #ff982d;
}
.platform-win32 .quokka-panel-view .quokka-panel-view-resizer {
  cursor: ns-resize;
}
