css:
#status-bar-jumpy {
  color: #5293d8;
}
#status-bar-jumpy.no-match,
#status-bar-jumpy.no-match .status {
  color: #ff982d;
}
.jumpy-label {
  color: #292c2f;
  background: #aaa;
  position: absolute;
  vertical-align: bottom;
  transform: translate(0, -100%);
  padding: .15em;
  border-radius: 2px;
  font-family: 'BlinkMacSystemFont', 'Lucida Grande', 'Segoe UI', Ubuntu, Cantarell, sans-serif;
}
.jumpy-label.irrelevant {
  visibility: hidden;
}
.jumpy-label.high-contrast {
  background-color: #17ca65;
}
.jumpy-label.tab-label {
  margin-left: 10px;
}
.beacon {
  display: block;
  position: absolute;
  border-radius: 1px;
  width: 2px;
  height: 2px;
  z-index: -1;
  -webkit-animation: ripple .085s;
  content: '';
  top: -6px;
}
.beacon.tab-beacon {
  left: 18px;
  top: -15px;
}
@-webkit-keyframes ripple {
  0% {
    box-shadow: 0 0 0 0 transparent,
                   0 0 0 0 transparent,
                   0 0 0 0 transparent,
                   0 0 0 0 transparent;
  }
  5% {
    box-shadow: 0 0 0 0 #c00, 0 0 0 0 rgba(255, 255, 255, 0.5), 0 0 0 0 #c00, 0 0 0 0 rgba(0, 0, 0, 0.1);
  }
  100% {
    box-shadow: 0 0 4px 24px #c00, 0 0 0 0 transparent, 0 0 0 0 transparent, 0 0 0 0 transparent;
  }
}
