css:
todo {
  width: 400px;
  overflow-y: scroll;
}
todo .header {
  display: flex;
  justify-content: flex-end;
  margin-bottom: 0.5rem;
}
todo .header h1 {
  flex: 1;
  margin: 0;
  padding: 0;
}
todo .header .controls {
  padding: 0;
  margin: 0;
}
todo .search {
  background-color: #212224;
  border-color: rgba(0, 0, 0, 0.5);
  border: 0;
  width: 100%;
  padding: 0.5em;
}
todo ::-webkit-input-placeholder {
  /* WebKit, Blink, Edge */
  color: #555;
}
todo :-moz-placeholder {
  /* Mozilla Firefox 4 to 18 */
  color: #555;
  opacity: 1;
}
todo ::-moz-placeholder {
  /* Mozilla Firefox 19+ */
  color: #555;
  opacity: 1;
}
todo :-ms-input-placeholder {
  /* Internet Explorer 10-11 */
  color: #555;
}
todo .status {
  text-align: right;
  color: #5293d8;
}
todo .loading {
  margin: auto;
}
todo .list .list-item {
  margin-bottom: 10px;
}
todo .list .list-item .filepath {
  color: #555;
  overflow: hidden;
  text-overflow: ellipsis;
}
todo .list .list-item .todo-text {
  color: #aaa;
  margin: 5px 0 5px 5px;
}
todo .list .list-item .todo-text:hover {
  cursor: pointer;
  color: #fff;
}
todo .icon-file-text,
todo .icon-file-directory {
  color: #555;
}
todo .list-item {
  white-space: normal;
}
